import React, {useEffect, useState} from 'react';
import axios from 'axios';
import NavBar from "../NavBar.js";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Table from './boardbackenddummy.js'
import "./Board.css"
import UserIcon from '@material-ui/icons/Person';

import {Link} from "react-router-dom";

function Comment() {
    const [posts, setPosts] = useState({user: []})
    const [idx, setIdx] = useState("")

    const fetchPostList = async () => {
        axios.defaults.headers.common['X-AUTH-TOKEN'] = localStorage.getItem("auth")
        const token = localStorage.getItem('auth')
        let web = "http://ec2-3-35-154-116.ap-northeast-2.compute.amazonaws.com:8080/api/admin/community/" + localStorage.getItem("boardId")
        const response = await axios.get(web, {
            headers: {
                "Content-Type": `application/json`
            }
        })
            .then(function (res) {
                console.warn(res.data)
                console.warn(res.data.data[1].boardId)
                console.warn(token)
                if(idx==="")setPosts({data: res.data.data})
                else {
                    let flag = 0
                    for(let i = 0; i < res.data.data.length; i++) {
                        if(res.data.data[i].boardId == idx) {
                            flag = 1
                            setPosts({data: [res.data.data[i]]})
                            console.warn(res.data.data[i])
                        }
                    }
                    if(flag === 0) setPosts({data: res.data.data})
                }
                console.warn(posts)
                console.warn(idx === "")
            })
            .catch(function (error) {
                console.warn(error)
                console.warn(token)
            })
    }

    useEffect(() => {
        fetchPostList()
    }, [setPosts])

    return (
        <div className="front">
            <NavBar className="top" />
            <h1>댓글</h1>
        </div>
    );
}

export default Comment;
