import React, {useEffect, useState} from 'react';
import axios from 'axios';
import NavBar from "../NavBar.js";
import './users.css';
import './userUpdate.css';

import {Link} from "react-router-dom";

function UserInfo() {
    const [posts, setPosts] = useState({user: []})
    const fetchPostList = async () => {
        console.warn("!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!")
        console.warn(localStorage.getItem("userId"))
        axios.defaults.headers.common['X-AUTH-TOKEN'] = localStorage.getItem("auth")
        const token = localStorage.getItem('auth')
        let web = "http://ec2-3-35-154-116.ap-northeast-2.compute.amazonaws.com:8080/api/admin/user/" + localStorage.getItem("userId")
        const response = await axios.get(web, {
            headers: {
                "Content-Type": `application/json`
            }
        })
            .then(function (res) {
                console.warn(res.data)
                console.warn(token)
                setPosts({data: res.data.data})
            })
            .catch(function (error) {
                console.warn(error)
                console.warn(token)
            })


        console.warn(posts)
    }

    useEffect(() => {
        fetchPostList()
    }, [setPosts])

    return (
        <div className="front">
            <NavBar class="top" />
            <div>{posts.id}</div>
            <div>{posts.name}</div>
        </div>
    );
}

export default UserInfo;
