import React, { useState, useEffect, Component } from "react";
import NavBar from "../NavBar.js";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import './users.css';
import './userUpdate.css';
import axios from "axios";

import {Link} from "react-router-dom";

function UserUpdate() {
    const [name, setName] = useState("")
    const [email, setEmail] = useState("")
    const [point, setPoint] = useState("")
    const [mPlant, setmPlant] = useState("")
    const [subscr, setSubscr] = useState("")
    const [posts, setPosts] = useState("")

    async function update() {
        console.warn(name, email, point, mPlant, subscr)
        let item = {name, email, point, mPlant, subscr}
    }

    async function getValue() {
        console.warn(localStorage.getItem("userId"))
        axios.defaults.headers.common['X-AUTH-TOKEN'] = localStorage.getItem("auth")
        const token = localStorage.getItem('auth')
        let web = "http://ec2-3-35-154-116.ap-northeast-2.compute.amazonaws.com:8080/api/admin/user/" + localStorage.getItem("userId")
        const response = await axios.get(web, {
            headers: {
                "Content-Type": `application/json`
            }
        })
            .then(function (res) {
                console.warn([res.data.data])
                setPosts({data: [res.data.data]})
            })
    }

    useEffect(() => {
        getValue()
    }, [])

    return (
        <div className="front">
            <NavBar class="top" />
            <h1>유저 업데이트</h1>
            <div className={"left"}>
                이름 <Form.Control className={"inline2"} type="text" placeholder={posts.data.data.email} style={{width: "450px"}}/><br/>
                이메일 <Form.Control className={"inline2"} type="text" placeholder="Normal text" style={{width: "450px"}}/><br/>
                포인트<Form.Control className={"inline2"} type="text" placeholder="Normal text" style={{width: "450px"}}/><br/>
                식물슬롯개수<Form.Control className={"inline2"} type="text" placeholder="Normal text" style={{width: "450px"}}/><br/>
                구독여부<Form.Control className={"inline2"} type="text" placeholder="Normal text" style={{width: "450px"}}/><br/>
                <Link to="/administrator/user">
                <Button variant="danger">뒤로 가기</Button>
                </Link>
                <Link to="/administrator/user">
                <Button variant="success">수정</Button>
                </Link>
            </div>
        </div>
    );
}

export default UserUpdate;
